import UIKit
import JFLib

class MyWinesWorker {
    let wineRepository: WineRepository
    
    init(wineRepository: WineRepository) {
        self.wineRepository = wineRepository
    }

    func fetchMyWines(completion: @escaping (Result<[Wine]>) -> ()) {
        wineRepository.getMyWines(completion: completion)
    }
}
